client_script:
var otp="";
var key_value = frappe.datetime.get_datetime_as_string();
var is_valid_email=false;
var is_valid_subdomain = false;
var is_valid_form = false;

frappe.web_form.set_value("key",key_value);
$(frappe.web_form.get_field("password").input).attr('type', 'password');
$(frappe.web_form.get_field("confirm_password").input).attr('type', 'password')

/*
 * Customize webform design to incorporate
 */

$(".page-header-actions-block").hide();
$(".web-form-footer").removeClass("pull-right");
$(".web-form-footer").css({"text-align":"Center"});
$("#page-saas-signup").css({"margin":"0 auto", "max-width":"600px"});
$("main").addClass("card card-body");

frappe.web_form.on('subdomain', (field, value) => {
    frappe.call({
        method: "better_saas.better_saas.doctype.saas_user.saas_user.check_subdomain_avai",
        args: {
            "subdomain": value
        },
        callback: function(r){
            if (value != "") {
                if (r.message.status === "True") {
                    $(field.wrapper).find(".help-box").text("Subdomain available");
                    $(field.wrapper).find(".help-box").attr("style", "color: green !important");
                    is_valid_subdomain = true;
                    initOTP();
                }
                if (r.message.status === "False") {
                    frappe.web_form.set_value("subdomain", "");
                    is_valid_subdomain = false;
                    $(field.wrapper).find(".help-box").text("Subdomain not available");
                    $(field.wrapper).find(".help-box").attr("style", "color: red !important");
                }
            } else {
                $(field.wrapper).find(".help-box").text();
            }

        }
    })
});
frappe.web_form.on('email', (field, value) => {
    frappe.call({
        method: "better_saas.better_saas.doctype.saas_user.saas_user.check_email_avai",
        args: {
            "email": value        
        },
        callback: function(r){
            if (value != "") {
                if (r.message.status === "True") {
                    $(field.wrapper).find(".help-box").text("Valid Email");
                    $(field.wrapper).find(".help-box").attr("style", "color: green !important");
                    is_valid_email = true;
                    initOTP();
                }
                if (r.message.status === "False") {
                    is_valid_email = false;
                    frappe.web_form.set_value("email", "");
                    $(field.wrapper).find(".help-box").text("Email Already Taken");
                    $(field.wrapper).find(".help-box").attr("style", "color: red !important");
                }
            } else {
                $(field.wrapper).find(".help-box").text();
            }
        }
    })
});


frappe.web_form.on('mobile', (field, value) => {
 initOTP();   
});

frappe.web_form.validate = () => {
	if(frappe.web_form.get_field("password").value != frappe.web_form.get_field("confirm_password").value){
		frappe.throw("Passwords Dont Match !!")
		frappe.web_form.set_value("confirm_password", "");
		return false;
    }

    if(!is_valid_email){
        frappe.throw("Please enter valid Email.");
        return false;
    }

    if(!is_valid_subdomain){
        frappe.throw("Please enter valid subdomain.");
        return false;
    }

    if(frappe.web_form.get_value("otp")!=otp){
        frappe.throw("Please enter a valid OTP");
        return false;
    }
}

function initOTP(){
    let mobile = frappe.web_form.get_value('mobile');
    if(mobile.length==10 && is_valid_email && is_valid_subdomain){
        frappe.web_form.set_df_property('otp', 'hidden', false);
        generateOTP();
        sendOTP();
    }
}

function generateOTP() {
    var string = '0123456789';
    var len = string.length;
    for (let i = 0; i < 4; i++) {
        otp += string[Math.floor(Math.random() * len)];
    }
}

function sendOTP(){
    var message = otp+" is OTP to complete your registration for onehash.";
    var email = frappe.web_form.get_value("email");
    var mobile = [];
    mobile.push(frappe.web_form.get_value("mobile"));
    frappe.call({
        method: "frappe.core.doctype.communication.email.make",
        args: {
            recipients: email,
            sender:"alerts@onehash.ai",
            content: message,
            subject: "Verify Account",
            send_email: 1,
            communication_medium: "Email"
        },
        callback: function(r) {
            if (r.message.status === "True") {
                show_alert("Please check your email or Mobile for OTP.");
            }
        }
    });

    frappe.call({
            method: "frappe.core.doctype.sms_settings.sms_settings.send_sms",
            args: {
                "receiver_list": mobile,
                "msg": message
            }
    });
}
